<?php/*FILE: single-work.php* DESCRIPTION: The template for displaying a single reading list item*/require('render-work.php');$wprl_options = get_option('wprl_plugin_options');get_header(); ?>    <main class="wprl-container" role="main">        <section class="wprl-full-screen-grid-container wprl-single-container">            <?php if (have_posts()) {                while (have_posts()) {                    the_post();                    renderPost(false);                }                comments_template();            }            else { ?>                <h2 class="entry-header"><?php _e('No Results: ', 'wp_reading_list');?></h2>            <?php } ?>        </section>    </main><?php get_sidebar();get_footer();