<?php/*FILE: taxonomy-work-type.php*DESCRIPTION: The template for displaying a work type archive*/require('render-work.php');$value = get_queried_object();$wprl_options = get_option('wprl_plugin_options');get_header(); ?>    <main class="wprl-container" role="main">        <h1 class="wprl-title">            <?php _e('Archive for', 'wp_reading_list');  echo (' '.$value->name);?>        </h1>        <section class="wprl-full-screen-grid-container wprl-taxonomy-container">            <?php if ( have_posts() ){ ?>                <div class="wprl-row">                    <?php while (have_posts()) {                        the_post();                        renderPost();                    } ?>                </div>                <?php                renderNavigation();            }            else {                renderNoResults();            } ?>        </section>    </main><?php get_sidebar();get_footer(); ?>